# check_imports.py\n\"\"\"\nüîç –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ —ñ–º–ø–æ—Ä—Ç—ñ–≤ –¥–ª—è H3 Modal API\n\"\"\"\n\nimport sys\nimport os\nfrom pathlib import Path\n\n# –î–æ–¥–∞—î–º–æ src –¥–æ path\nsrc_path = Path(__file__).parent / \"src\"\nsys.path.insert(0, str(src_path))\n\nprint(f\"üîß –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ —ñ–º–ø–æ—Ä—Ç—ñ–≤ H3 Modal API\")\nprint(f\"üìÅ Working directory: {Path.cwd()}\")\nprint(f\"üìÇ Source path: {src_path}\")\nprint(f\"üêç Python version: {sys.version}\")\nprint(\"=\"*60)\n\n# –°–ø–∏—Å–æ–∫ –º–æ–¥—É–ª—ñ–≤ –¥–ª—è –ø–µ—Ä–µ–≤—ñ—Ä–∫–∏\nmodules_to_check = [\n    (\"fastapi\", \"FastAPI framework\"),\n    (\"uvicorn\", \"ASGI server\"),\n    (\"pydantic\", \"Data validation\"),\n    (\"psycopg2\", \"PostgreSQL driver\"),\n    (\"h3\", \"H3 hexagonal indexing\"),\n    (\"typing\", \"Type hints (built-in)\"),\n    (\"datetime\", \"Date/time (built-in)\"),\n    (\"json\", \"JSON handling (built-in)\"),\n    (\"logging\", \"Logging (built-in)\"),\n]\n\nprint(\"üì¶ –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ –∑–æ–≤–Ω—ñ—à–Ω—ñ—Ö –∑–∞–ª–µ–∂–Ω–æ—Å—Ç–µ–π:\")\nimport_errors = []\n\nfor module_name, description in modules_to_check:\n    try:\n        __import__(module_name)\n        print(f\"‚úÖ {module_name:<15} - {description}\")\n    except ImportError as e:\n        print(f\"‚ùå {module_name:<15} - –í–Ü–î–°–£–¢–ù–Ü–ô: {e}\")\n        import_errors.append(module_name)\n\nprint(\"\\n\" + \"=\"*60)\nprint(\"üß© –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ –Ω–∞—à–∏—Ö –º–æ–¥—É–ª—ñ–≤:\")\n\n# –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ –Ω–∞—à–∏—Ö –º–æ–¥—É–ª—ñ–≤\nour_modules = [\n    (\"main\", \"–ì–æ–ª–æ–≤–Ω–∏–π FastAPI –¥–æ–¥–∞—Ç–æ–∫\"),\n    (\"api.endpoints.h3_modal_endpoints\", \"H3 Modal endpoints\"),\n    (\"api.endpoints.h3_visualization\", \"H3 Visualization endpoints\"),\n]\n\nour_import_errors = []\n\nfor module_name, description in our_modules:\n    try:\n        module = __import__(module_name, fromlist=[''])\n        print(f\"‚úÖ {module_name:<35} - {description}\")\n        \n        # –î–æ–¥–∞—Ç–∫–æ–≤–∞ —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ—è –ø—Ä–æ –º–æ–¥—É–ª—å\n        if hasattr(module, 'router'):\n            print(f\"   ‚îî‚îÄ Router –¥–æ—Å—Ç—É–ø–Ω–∏–π\")\n        if hasattr(module, 'app'):\n            print(f\"   ‚îî‚îÄ FastAPI app –¥–æ—Å—Ç—É–ø–Ω–∏–π\")\n            \n    except ImportError as e:\n        print(f\"‚ùå {module_name:<35} - –ü–û–ú–ò–õ–ö–ê: {e}\")\n        our_import_errors.append((module_name, str(e)))\n    except Exception as e:\n        print(f\"‚ö†Ô∏è {module_name:<35} - –Ü–ù–®–ê –ü–û–ú–ò–õ–ö–ê: {e}\")\n        our_import_errors.append((module_name, str(e)))\n\nprint(\"\\n\" + \"=\"*60)\nprint(\"üìä –ü–Ü–î–°–£–ú–û–ö:\")\n\nif not import_errors and not our_import_errors:\n    print(\"üéâ –í–°–Ü –Ü–ú–ü–û–†–¢–ò –£–°–ü–Ü–®–ù–Ü!\")\n    print(\"‚úÖ API –≥–æ—Ç–æ–≤–µ –¥–æ –∑–∞–ø—É—Å–∫—É\")\n    print(\"üí° –°–ø—Ä–æ–±—É–π—Ç–µ: python start_test_server.py\")\nelse:\n    print(\"‚ö†Ô∏è –ó–ù–ê–ô–î–ï–ù–Ü –ü–†–û–ë–õ–ï–ú–ò:\")\n    \n    if import_errors:\n        print(f\"\\n‚ùå –í—ñ–¥—Å—É—Ç–Ω—ñ –∑–æ–≤–Ω—ñ—à–Ω—ñ –±—ñ–±–ª—ñ–æ—Ç–µ–∫–∏ ({len(import_errors)}):\")\n        for lib in import_errors:\n            print(f\"   - {lib}\")\n        print(\"\\nüí° –í—Å—Ç–∞–Ω–æ–≤—ñ—Ç—å –∑–∞–ª–µ–∂–Ω–æ—Å—Ç—ñ:\")\n        print(f\"   pip install {' '.join(import_errors)}\")\n    \n    if our_import_errors:\n        print(f\"\\n‚ùå –ü–æ–º–∏–ª–∫–∏ –Ω–∞—à–∏—Ö –º–æ–¥—É–ª—ñ–≤ ({len(our_import_errors)}):\")\n        for module_name, error in our_import_errors:\n            print(f\"   - {module_name}: {error}\")\n\nprint(\"\\n\" + \"=\"*60)\nprint(\"üîç –î–æ–¥–∞—Ç–∫–æ–≤–∞ –¥—ñ–∞–≥–Ω–æ—Å—Ç–∏–∫–∞:\")\n\n# –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ —Ñ–∞–π–ª–æ–≤–æ—ó —Å—Ç—Ä—É–∫—Ç—É—Ä–∏\nkey_files = [\n    \"src/main.py\",\n    \"src/api/endpoints/h3_modal_endpoints.py\",\n    \"src/api/endpoints/h3_visualization.py\",\n    \"src/api/endpoints/__init__.py\",\n    \"requirements.txt\"\n]\n\nprint(\"\\nüìÅ –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ —Ñ–∞–π–ª—ñ–≤:\")\nfor file_path in key_files:\n    full_path = Path(file_path)\n    if full_path.exists():\n        size = full_path.stat().st_size\n        print(f\"‚úÖ {file_path:<40} ({size} –±–∞–π—Ç)\")\n    else:\n        print(f\"‚ùå {file_path:<40} –í–Ü–î–°–£–¢–ù–Ü–ô\")\n\nprint(\"\\nüîó Python path:\")\nfor i, path in enumerate(sys.path[:5]):  # –ü–æ–∫–∞–∑—É—î–º–æ –ø–µ—Ä—à—ñ 5 —à–ª—è—Ö—ñ–≤\n    print(f\"   {i}: {path}\")\n\nprint(\"\\nüèÅ –ü–µ—Ä–µ–≤—ñ—Ä–∫—É –∑–∞–≤–µ—Ä—à–µ–Ω–æ!\")\nif not import_errors and not our_import_errors:\n    print(\"üöÄ –ì–æ—Ç–æ–≤—ñ –¥–æ —Ç–µ—Å—Ç—É–≤–∞–Ω–Ω—è API!\")\nelse:\n    print(\"üîß –°–ø–æ—á–∞—Ç–∫—É –≤–∏–ø—Ä–∞–≤—Ç–µ –ø–æ–º–∏–ª–∫–∏ —ñ–º–ø–æ—Ä—Ç—É\")\n